t.script:
document.addEventListener("DOMContentLoaded", function() {

    function handleViaChange() {
        var viaSelect = document.getElementById("via");
        var agentIdRow = document.getElementById("agent_id");
        var agentIdLabel = document.getElementById("label_agent");

        if (viaSelect.value === "agent") {
            agentIdRow.style.display = "block";
            agentIdLabel.style.display = "block";
        } else {
            agentIdRow.style.display = "none";
            agentIdLabel.style.display = "none";
        }
    }

    var viaSelect = document.getElementById("via");
    if (viaSelect) {
        viaSelect.addEventListener("change", handleViaChange);
    }

    handleViaChange();
});

