t.script:
document.getElementById('via').addEventListener('change',
function () {
var agentIdGroup =
document.getElementById('agent_id_group');
if (this.value === 'agent') {
agentIdGroup.style.display = 'block';
} else {
agentIdGroup.style.display = 'none';
}
});

